import { StyleSheet, Text, TouchableOpacity } from "react-native"



const MobiButton = ( { titulo, corBotao, corTexto, children} )=>{

    //Style
    const estilo = StyleSheet.create({
        botao:{
            backgroundColor: corBotao,
            padding: 15,
            borderRadius: 10,
        },
        texto:{
            color: corTexto,
        }
    })

    console.log('##### props')

    const func1 = ()=> {
        console.log("Fui Apertado!!!!!!!")
    }

    return(
        <TouchableOpacity style={estilo.botao} onPress={func1}>
            
            <Text style={estilo.texto}> {children} </Text>

        </TouchableOpacity>
    )

}



export default MobiButton
